def print_distribution (d, title, labels):
    print ("#### %s" % title)
    print ("| %s | %s |" % labels)  
    print ("|:---:|:---:|")
    for i in range (len (d)):
        r = d["%d" % i]
        print ("| %.4g | %.4g |" % (r[labels[0]], r[labels[1]]))  

def extract_distribution (d, labels):
    result = []
    for i in range (len (d)):
        r = d["%d" % i]
        result.append ([r[labels[0]], r[labels[1]]])

    return result

